b:includable.script:
// 
    var maxRelatedPosts = 3;
    var relatedPostURLs = [];
    var relatedPostTitles = [];
    var relatedPostSnippets = [];
    var relatedPostImages = [];

    function relatedPosts(data) {
        jQuery(document).ready(function($) {
            relatedPostDataPopulate(data);

            $('.related-posts').each(function() {
                var markup = ''; // Initialize.
                var indexes = relatedPostRandomIndexes();

                for (var i = 0; i < indexes.length; i++) {
                    var url = relatedPostURLs[indexes[i]];
                    var title = relatedPostTitles[indexes[i]];
                    var snippet = relatedPostSnippets[indexes[i]];
                    var image = relatedPostImages[indexes[i]];

                    markup += '<li class="related-post-item">';
                    markup += '  <a class="related-post-thumbnail" href="' + url + '" title="' + title + '">';
                    markup += '     <figure><img src="' + image + '" /></figure>';
                    markup += '  </a>';
                    markup += '  <h5><a href="' + url + '">' + title + '</a></h5>';
                    markup += '  <p>' + snippet + '</p>';
                    markup += '</li>';
                }
                if (markup) {
                    $(this).find('.related-posts-inner').html(markup);
                } else {
                    $(this).remove();
                }
            });
        });
    }
    function relatedPostDataPopulate(data) {
        relatedPostURLs = [];
        relatedPostTitles = [];
        relatedPostSnippets = [];
        relatedPostImages = [];

        for (var i = 0; i < data.feed.entry.length; i++) {
            var title = '', snippet = '', image = '';
            var entry = data.feed.entry[i];

            if ('title' in entry) {
                title = entry.title.$t;
            }
            if ('content' in entry) {
                snippet = relatedPostContentSnip(entry.content.$t, 100);
            } else if ('summary' in entry) {
                snippet = relatedPostContentSnip(entry.summary.$t, 100);
            }
            if ('media$thumbnail' in entry) {
                image = entry.media$thumbnail.url
                    .replace(/\/s72(?:-[wh][0-9]+){0,2}(?:-[cw])?\//, '/s640/')
                    .replace('/default.jpg', '/maxresdefault.jpg');
            } else {
                image = 'data:image/jpeg;base64,';
            }
            if (title && snippet && image && entry.link.length) {
                for (var l = 0; l < entry.link.length; l++) {
                    var link = entry.link[l];

                    if (link.rel !== 'alternate' || link.type !== 'text/html') {
                        continue; // Not the URL we are looking for.
                    }
                    relatedPostURLs.push(link.href);
                    relatedPostTitles.push(title);
                    relatedPostSnippets.push(snippet);
                    relatedPostImages.push(image);

                    break; // All done.
                }
            }
        }
    }
    function relatedPostRandomIndexes() {
        var indexes = []; // Initialize.
        var maxIndexes = Math.min(maxRelatedPosts, relatedPostURLs.length);

        var validIndexes = relatedPostURLs
            .map(function(_, index) { return index; })
            .filter(function(index) { return relatedPostURLs[index] !== document.URL; });

        while (indexes.length < maxIndexes && validIndexes.length > 0) {
            var randomIndex = Math.floor(Math.random() * validIndexes.length);
            indexes.push(validIndexes[randomIndex]);
            validIndexes.splice(randomIndex, 1);
        }
        return indexes;
    }
    function relatedPostContentSnip(markup, maxLength) {
        var parts = markup.split('<');

        for (var i = 0, gtIndex = -1; i < parts.length; i++) {
            if ((gtIndex = parts[i].indexOf('>')) !== -1) {
                parts[i] = parts[i].substring(gtIndex + 1, parts[i].length);
            }
        }
        return parts.join('').substring(0, maxLength - 1);
    }
//

body.script:
// 
    jQuery(document).ready(function($) {
        /* Implements header actions.
        ------------------------------------------------------ */
        $('.header-outer').each(function() {
            var $header = $(this);

            $header.find('.toggle-menu').click(function() {
                $header.find('.header-menu').toggleClass('active');
            });
            $header.find('.toggle-search').click(function() {
                $header.find('.header-search').toggleClass('active');
                setTimeout(function() { $header.find('.search-input input').focus(); }, 500);
            });
            $header.find('.header-search .widget-content').click(function(e) {
                e.stopPropagation();
            });
        });
        /* Observes changes to comments.
        ------------------------------------------------------ */
        $('.comments').each(function() {
            var s220 = function(i, src) {
                return src.replace(/\/s35(?:-[wh][0-9]+){0,2}(?:-[cw])?\//, '/s220/');
            };
            (new MutationObserver(function(mutations) {
                for (var m of mutations) if (m.type === 'childList' && m.addedNodes.length) {
                    $(m.target).closest('.comments').find('.avatar-image-container img').attr('src', s220);
                }
            })).observe(this, { subtree: true, childList: true });

            $(this).find('.avatar-image-container img').attr('src', s220);
        });

        /* Sets subscription form action.
        /* --------------------------------------------------- */
        $('.SubscribeForm .subscribe-form-widget form').attr('action', function() {
            return $(this).find('input[type="hidden"][data-action]').data('action');
        });

        /* Prepares content image expansions.
        ------------------------------------------------------ */
        $(window).on('resize', function() {
            var bodyWidth = $('body').innerWidth();

            $('.post-body img[alt*="wide image"], .post-body img[alt*="full image"]').each(function(i) {
                $(this).parents('.tr-caption-container').css({
                    'width': bodyWidth,
                    'margin-right': 'calc(50% - ' + String(bodyWidth / 2) + 'px)',
                    'margin-left': 'calc(50% - ' + String(bodyWidth / 2) + 'px)',
                });
            });
            $('.post-body img[alt*="wide image"]').each(function(i) {
                $(this).parents('.tr-caption-container').addClass('img-gallery-wide');
            });
            $('.post-body img[alt*="full image"]').each(function(i) {
                $(this).parents('.tr-caption-container').addClass('img-gallery-full');
            });
        }).trigger('resize');
    });
//
